import React, { useState } from "react";
import About from "./components/About";
import Header from "./components/Header";
import Nav from "./components/Nav";

function App() {
  const [currentPage, setCurrentPage] = useState("about");

  const [currentCategory, setCurrentCategory] = useState(categories[0]); //what does [0] do?
  //it will set the current category to the first category in the array which is "About Me"
  //so when page loads it will display the About Me page

  return (
    //make a div styled with tailwind
    <div>
      <Header></Header>
      <Nav
        categories={categories} //when the nav component is rendered, it will pass in the categories array using the categories prop
        setCurrentCategory={setCurrentCategory} //when the user clicks on a Portfolio, Contact, Resume, etc. this will set the current category to the category that was clicked on
        currentCategory={currentCategory} //when the page loads it will display the About Me page
      ></Nav>
      <main>
        <About></About>
      </main>
    </div>
  );
}

export default App;
