import React, { useState } from "react";
import { validateEmail } from "../../utils/helpers";

function ContactForm() {
  const [formState, setFormState] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [errorMessage, setErrorMessage] = useState("");

  const { name, email, message } = formState;

  function handleChange(e) {
    if (e.target.name === "email") {
      const isValid = validateEmail(e.target.value);
      if (!isValid) {
        setErrorMessage("Please enter a valid email address");
      } else {
        setErrorMessage("");
      }
    } else {
      if (!e.target.value.length) {
        setErrorMessage(`${e.target.name} is required`);
      } else {
        setErrorMessage("");
      }
      //   console.log("errorMessage", errorMessage)
    }
    if (!errorMessage) {
      //spread operator ...
      setFormState({ ...formState, [e.target.name]: e.target.value });
      console.log("Handle Form", formState);
    }
  }

  return (
    <div className="">
      <h1 id="contact-title" className="">
        Contact me
      </h1>
      <section id="" className="">
        <form id="form-control" className="">
          <div id="" className="">
            <label id="name" htmlFor="name" classname="">
              Name:
            </label>
            <input
              className="form-input"
              type="text"
              name="name"
              onBlur={handleChange}
              defaultValue={name}
            />
          </div>
          <div id="" className="">
            <label id="email" htmlFor="email" className="">
              Email:
            </label>
            <input
              className="form-input"
              type="email"
              name="email"
              onBlur={handleChange}
              defaultValue={email}
            />
          </div>
          <div id="" className="">
            <label id="message" htmlFor="message" className="">
              Message:
            </label>
            <textarea
              className="form-input"
              name="message"
              rows="5"
              onBlur={handleChange}
              defaultValue={message}
            />
          </div>
          {errorMessage && (
            <div>
              <p className="error-text">{errorMessage}</p>
            </div>
          )}
          <button className="" type="submit">
            Send
          </button>
          {/* //my email thats clickable and my phone number thats clickable */}
          <p id="contact-icons-style" className="contact-info flex flex-wrap">
            <a href="mailto:jhack00@icloud.com " className="flex flex-row">
              <img
                src="./../images/email.png"
                alt="email"
                class="animate-pulse"
              />
              <span id="orange">jhack00@icloud.com</span>
            </a>
            <a href="tel:+1-512-779-1970" className="flex">
              <img
                src="./../images/phone.png"
                alt="phone"
                className="animate-pulse"
              />
              <span id="orange">512-779-1870</span>
            </a>
          </p>
        </form>
      </section>
    </div>
  );
}

export default ContactForm;
